import React from 'react';

const UserPanel = ({ users = [], roomId }) => {
  const getInitials = (name) => {
    return name
      .split(' ')
      .map(word => word.charAt(0))
      .join('')
      .toUpperCase()
      .slice(0, 2);
  };

  const getRandomColor = (name) => {
    const colors = [
      '#667eea', '#764ba2', '#f093fb', '#f5576c',
      '#4facfe', '#00f2fe', '#43e97b', '#38f9d7',
      '#fa709a', '#fee140', '#a8edea', '#fed6e3'
    ];
    const index = name.charCodeAt(0) % colors.length;
    return colors[index];
  };

  return (
    <div className="user-panel">
      <div className="user-panel-header">
        <h3>Active Users</h3>
        <div className="user-count">{users.length}</div>
        <div className="user-count-label">
          {users.length === 1 ? 'user' : 'users'} online
        </div>
      </div>
      
      <div className="users-list">
        {users.length === 0 ? (
          <div className="empty-state">
            <div className="empty-state-icon">üë•</div>
            <p>No users connected</p>
          </div>
        ) : (
          users.map((user, index) => (
            <div key={user.id || index} className="user-item">
              <div 
                className="user-avatar"
                style={{ background: getRandomColor(user.name || 'User') }}
              >
                {getInitials(user.name || 'User')}
              </div>
              <div className="user-info">
                <div className="user-name">{user.name || `User ${index + 1}`}</div>
                <div className="user-status">
                  <div className="status-dot"></div>
                  Online
                </div>
              </div>
            </div>
          ))
        )}
      </div>
      
      <div className="controls-panel">
        <div className="control-group">
          <h4>Room Info</h4>
          <div className="room-id">{roomId || 'Default Room'}</div>
        </div>
        
        <div className="control-group">
          <h4>Drawing Tools</h4>
          <div className="control-buttons">
            <button className="control-btn active">‚úèÔ∏è Pencil</button>
            <button className="control-btn">üî¥ Circle</button>
            <button className="control-btn">‚¨ú Rectangle</button>
          </div>
        </div>
        
        <div className="control-group">
          <h4>Actions</h4>
          <div className="control-buttons">
            <button className="control-btn">üóëÔ∏è Clear</button>
            <button className="control-btn">üíæ Save</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserPanel;
